import { createSlice } from "@reduxjs/toolkit";
const initialState = {
    item:[],
    itemLength:0
}

const AddtoCartSlice = createSlice({
   name:'addToCart',
   initialState,
   reducers:{
    addtoCartHandler(state, action){
        state.itemLength = state.item.length + 1;
        let findItem = action.payload.product;
        
        // console.log(state.item.length)
        // if(state.item.length == 0 ){
        //     console.log(state.item)
        //     state.item.push(findItem);

        // }
        const findEleIndex = state.item.find((item)=>{
            // console.log(findItem)
            return  item.id === findItem.id
    }) 


        
        
        state.item.push(action.payload.product);
  
    },


    
    deleteItemHandler(state, action){
        const filterItem = state.item.filter((prd)=>{
            return prd.id != action.payload

        })        
        state.item = filterItem



        }


   } 
})

export const {addtoCartHandler, deleteItemHandler} = AddtoCartSlice.actions

export default AddtoCartSlice.reducer
