import { createSlice } from "@reduxjs/toolkit";
const initialState = {
    item:[],
    itemLength:0
}

const AddtoCartSlice = createSlice({
   name:'addToCart',
   initialState,
   reducers:{
    // addtoCartHandler(state, action){
    //     state.itemLength = state.item.length + 1;
    //     state.item.push(action.payload.product);           

    //     },

    addtoCartHandler(state, action){
        const findEle = state.item.find((data)=>{
                 return data.id === action.payload.product.id
        })
        if(findEle){
            findEle.quantity++;
            state.itemLength = state.item.length;
        }
        else{
            state.item.push({...action.payload.product, quantity:1 });
            state.itemLength = state.item.length;
        }


    },
    decreaseQuantity(state, action){
        console.log(action.payload)
        const findEle = state.item.find((data)=>{
            return data.id === action.payload.product.id
        })
        if(findEle){
            findEle.quantity -= findEle.quantity;
        }
        


    },
    increaseQuanitity(state, action){
        const findEle = state.item.find((data)=>{
            return data.id === action.payload.product.id
        })
        if(findEle){
            findEle.quantity = findEle.quantity + 1;
            console.log(findEle)
        }

   
        // state.totalQuantity += action.payload
        // console.log(action.payload)

    },
    deleteItemHandler(state, action){
        const filterItem = state.item.filter((prd)=>{
            return prd.id != action.payload
         
        })        
        state.item = filterItem



        }

    }
    
})

export const {addtoCartHandler, deleteItemHandler, decreaseQuantity, increaseQuanitity} = AddtoCartSlice.actions

export default AddtoCartSlice.reducer
