import React, { useEffect, useState } from 'react'
import './Prodcut.css'
import ProductList from './ProductList'

const Products = () => {

const [fetchProducts, setFetchProducts] = useState()
const [loading, setLoading] = useState(true);
const [page, setPage] = useState(1)



async function fetchData(){
  setLoading(true)
try{
  const response = await fetch('https://fakestoreapi.com/products')
  if(!response.ok){
    throw new Error('something went wrong')
  }
  const data = await response.json()
  const transformedData = data.map((prd)=>{
    return{
          id:prd.id,
          title:prd.title,
          description:prd.description,
          image:prd.image,
          price:prd.price,
          // rating:prd.rating.rate,
    }
  })
  setFetchProducts(transformedData)


  setLoading(false)
}
catch(err){
console.log(err)
}


}

const pageChangeHandler = (selectedPage)=>{
  if(selectedPage >= 1 && 
    selectedPage <= fetchProducts.length &&
    selectedPage !== page){
      setPage(selectedPage)}


}

useEffect(()=>{
  fetchData()
},[])


  return (
    <>
<h1>Our Products</h1>

    {loading && <p className='loading'>
      <span className='loader'></span>
      <span className='text'>Loading</span>
      </p>}
    
    {!loading && <ProductList 
    fetchProducts={fetchProducts} 
    changePage={pageChangeHandler}
    page={page}
   />
    }
    
    
    
      
      </>
      
      
  )
}

export default Products
