import React, {useState, useEffect } from 'react'
import {useParams} from "react-router-dom";
import './ProductDetail.css'
import QuantitySelect from '../Common/QuantitySelect/QuantitySelect';
import {useDispatch } from "react-redux";
import {addtoCartHandler} from './../../store/AddtoCartSlice'

const ProductDetail = () => {
  const disptch = useDispatch();
  const [data, setData] = useState();
  function addToCart(prd) {
     disptch(addtoCartHandler({product:prd}))
  }
  

  let { id } = useParams();

  const fetachProduct = async ()=>{
    const res = await fetch(`https://fakestoreapi.com/products/${id}`);
    const responseData = await res.json()

    setData(responseData) 
  }

  useEffect(() => {
    fetachProduct();
  }, []);


  return (
    <div className='container-fluid'>
<div className='row'>
<section className='col-lg-5'>
<div className='detailImg'>
   <img src={data?.image} alt={data?.title}/>

</div>
</section>
<section className='col-lg-7'>
  <article className='bgwhite'>
  <h1>{data?.title} 
  </h1>
  <p>{data?.description}</p>
  <p className='price'>Price: &#8377; {data?.price}</p>
  {/* <p className='rating'>Rating: {data?.rating}</p> */}
  <p className='category'>Category : {data?.category  }</p>
<QuantitySelect />
<button className='primaryBtn' onClick={()=>{addToCart(data)}}>Add to Cart</button>

</article>
  
  </section>

</div>


    </div>
  )
}

export default ProductDetail
