import React, { useState } from "react";


const QuantitySelect = ({ qty }) => {
  const [detailQty, setDetailQty] = useState();
  const [qtyError, setQtyError] = useState(false);

  function QtyHandlerBtn(type) {
    if (type === "minus" && detailQty > 1) {
      setDetailQty(detailQty - 1);
      setQtyError(false);
    } else if (type === "plus" && detailQty < 5) {
      setDetailQty(prevState => console.log(prevState));
      setQtyError(false);
    } else {
      return setQtyError(true);
    }
  }

  return (
    <>
      <div className="qty__container">
        <span>Quantity :</span>
        <p className="btn__group">
          <span
            className="btn minus"
            onClick={() => {
              QtyHandlerBtn("minus");
            }}
          >
            -
          </span>

          <span className="btn count">{qty >= 1 ? qty : detailQty}</span>
          <span
            className="btn plus"
            onClick={() => {
              QtyHandlerBtn("plus");
            }}
          >
            +
          </span>
        </p>
        {qtyError ? (
          <p className="text-danger">
            {"quantity should not less then 1 or more then 5"}
          </p>
        ) : (
          ""
        )}
      </div>
    </>
  );
};

export default QuantitySelect;
