import React, { useState } from "react";
import { useDispatch } from "react-redux";


const QuantitySelect = ({qty}) => {
//  const[qty, setQty] = useState()
const dispatch = useDispatch()
  const[qtyError, setQtyError] =  useState(false)



  function qtyDecreseHandlerBtn(type) {
    dispatch(decreaseQuanitity(type))
  }
  function qtyIncreseHandlerBtn(type) {
    dispatch(increaseQuanitity(type))
  }




  return (
    <>
      <div className="qty__container">
        <span>Quantity :</span>
        <p className="btn__group">
          <span className="btn minus" 
          // onClick={()=>{QtyHandlerBtn('minus')}}
          onClick={()=>{qtyDecreseHandlerBtn(qty)}}
          >
            -
          </span>

          <span className="btn count">{qty}</span>
          <span className="btn plus" 
          // onClick={()=>{QtyHandlerBtn('plus')}}
          
          onClick={()=>{qtyIncreseHandlerBtn(qty)}}

          >
            +
          </span>
         
        </p>
        {qtyError ? <p className="text-danger">{'quantity should not less then 1 or more then 5'}</p> : ''}
      </div>
    </>
  );
};

export default QuantitySelect;
